mapper.select:
select user_id,user_name, user_password, user_address, user_balance, flag
from user
where user_num = #{phonenum,jdbcType=VARCHAR}

mapper.update:
update user
user_name=#{userName}, user_password=#{userPassword}, user_num=#{userNum}, user_address=#{userAddress}, user_balance=#{userBalance}, flag=#{flag},
where user_id=#{userId}
